import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../css/RecomPage.css'; // CSS 파일 임포트
import logoImage from '../logo.png'; // 로고 이미지 경로 가정
import { FaSearch } from 'react-icons/fa'; // 돋보기 아이콘 사용을 위한 react-icons 임포트

function RecomPage() {
  const navigate = useNavigate();
  const [searchTerm, setSearchTerm] = useState('');

  const handleSearch = (e) => {
    if (e.key === 'Enter') {
      navigate('/movie-search', { state: { searchTerm } }); // 검색어를 가지고 Mvsrch 페이지로 이동
    }
  };

  return (
    <div className="RecomPage">
      <img src={logoImage} alt="Logo" className="logo"/>
      <button className="myPageButton" onClick={() => navigate('/my/watched')}>마이페이지</button>
      <div className="searchContainer">
        <FaSearch className="searchIcon"/>
        <input
          type="text"
          className="searchInput"
          placeholder="검색"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          onKeyDown={handleSearch}
        />
      </div>
      <div className="greetingText">Autumn님을 위한 취향저격 영화를 찾아봤어요.</div>
      <hr className="divider"/>
      <div className="recommendationText">이 영화, 틀림없이 좋아하실 거에요.</div>
    </div>
  );
}

export default RecomPage;
