import React, { useState } from 'react';
import '../css/Mvbanner.css'; 
import Popcho from '../pages/Popcho'; 

import watchaLogo from '../watcha.png';
import netflixLogo from '../netflix.png';
import disneyPlusLogo from '../disneyplus.png';
import wavveLogo from '../wavve.png'; 

const flatrateLogos = {
  'Watcha': watchaLogo,
  'Netflix': netflixLogo,
  'Disney Plus': disneyPlusLogo,
  'Wavve': wavveLogo
};

const MvBanner = ({ title, poster, flatrate, rating }) => {
  const [currentRating, setCurrentRating] = useState(rating); 
  const [showModal, setShowModal] = useState(false); 

  const handleStarClick = (index) => {
    setCurrentRating(currentRating === index + 1 ? currentRating - 1 : index + 1);
  };

  const handleAddClick = () => {
    setShowModal(true);
  };

  const handleCloseModal = () => {
    setShowModal(false);
  };

  const handleSaveModal = (option) => {
    console.log("저장 옵션:", option);
    setShowModal(false);
  };

  // 여기서 서버 URL과 poster 경로를 확인합니다.
  const posterUrl = `http://54.180.50.153:8080${poster}`;
  console.log(`Poster URL for ${title}: ${posterUrl}`);

  return (
    <div className="movie-banner" style={{ width: '154px', height: '273px' }}>
      <img src={posterUrl} alt={title} className="movie-poster" style={{ width: '154px', height: '231px' }} onError={(e) => e.target.src = 'https://via.placeholder.com/154x231?text=No+Image'} />
      <div className="movie-info" style={{ padding: '0 10px', height: '42px' }}>
        {flatrate.map((service, index) => (
          <img key={index} src={flatrateLogos[service]} alt={`${service} 로고`} 
               className="flatrate-logo"
               style={{ width: '18px', height: '18px' }} />
        ))}
        <div className="movie-title" style={{ fontSize: '10px', color: 'white' }}>{title}</div>
        <div className="movie-rating">
          {[...Array(5)].map((_, index) => (
            <span key={index} className={`star ${currentRating > index ? 'filled' : ''}`} style={{ fontSize: '14px' }}
              onClick={() => handleStarClick(index)}>
              ★
            </span>
          ))}
        </div>
        <button onClick={handleAddClick} className="add-button" style={{ fontSize: '25px' }}>+</button>
      </div>
      {showModal && <Popcho onClose={handleCloseModal} onSave={handleSaveModal} />}
    </div>
  );
};

export default MvBanner;
