import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../css/MvsrchPage.css'; 
import logoImage from '../logo.png'; 

function MvsrchPage() {
  const navigate = useNavigate();
  const [searchTerm, setSearchTerm] = useState('');
  const [showGenres, setShowGenres] = useState(false);
  const [showPlatforms, setShowPlatforms] = useState(false);
  const [selectedGenre, setSelectedGenre] = useState('All');
  const [selectedPlatform, setSelectedPlatform] = useState('All');

  const genres = ['All', 'Action', 'Adventure', 'Animation', 'Comedy', 
    'Crime', 'Documentary', 'Drama', 'Family', 'Fantasy', 
    'History', 'Horror', 'Music', 'Mystery', 'Romance', 
    'Sci-Fi', 'TV Movie', 'Thriller', 'War', 'Western'];

  const platforms = ['All', 'Netflix', 'Disney Plus', 'Watcha', 'Wavve'];

  const handleSearchClick = () => {
    navigate('/movie-search', { state: { searchTerm, selectedGenre, selectedPlatform } });
  };

  return (
    <div className="MvsrchPage">
      <img src={logoImage} alt="Logo" className="logo"/>
      <button className="myPageButton" onClick={() => navigate('/my/watched')}>마이페이지</button>
      <div className="searchContainer">
        <button onClick={() => setShowGenres(!showGenres)} className="genreButton">장르</button>
        {showGenres && (
          <div>
            {genres.map((genre) => (
              <button key={genre} onClick={() => setSelectedGenre(genre)} className="filter">
                {genre}
              </button>
            ))}
          </div>
        )}
        <button onClick={() => setShowPlatforms(!showPlatforms)} className="platformButton">플랫폼</button>
        {showPlatforms && (
          <div>
            {platforms.map((platform) => (
              <button key={platform} onClick={() => setSelectedPlatform(platform)} className="filter">
                {platform}
              </button>
            ))}
          </div>
        )}
        <input
          type="text"
          className="searchInput"
          placeholder="검색"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <button className="searchButton" onClick={handleSearchClick}>
          검색하기
        </button>
      </div>
    </div>
  );
}

export default MvsrchPage;
