import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Papa from 'papaparse';
import { Swiper, SwiperSlide } from 'swiper/react';
import SwiperCore, { Navigation, Pagination } from 'swiper';
import 'swiper/swiper-bundle.min.css';
import MvBanner from './MvBanner';
import '../css/RecomPage.css';
import logoImage from '../logo.png';
import moviesCSV from '../movie.csv';
import '../css/swiper-bundle.min.css';


SwiperCore.use([Navigation, Pagination]);

function RecomPage() {
  const navigate = useNavigate();
  const [searchTerm, setSearchTerm] = useState('');
  const [activeTab, setActiveTab] = useState('recommendations');
  const [movies, setMovies] = useState([]);
  const [randomMovies, setRandomMovies] = useState([]);

  useEffect(() => {
    Papa.parse(moviesCSV, {
      download: true,
      header: true,
      complete: (result) => {
        setMovies(result.data);
        setRandomMovies(shuffleArray(result.data).slice(0, 10));
      },
    });
  }, []);

  const shuffleArray = (array) => {
    let shuffled = array.slice();
    for (let i = shuffled.length - 1; i > 0; i--) {
      const j = Math.floor(Math.random() * (i + 1));
      [shuffled[i], shuffled[j]] = [shuffled[j], shuffled[i]];
    }
    return shuffled;
  };

  const handleSearchClick = () => {
    navigate('/movie-search', { state: { searchTerm } });
  };

  const topMovie = movies[0];

  return (
    <div className="RecomPage">
      <img src={logoImage} alt="Logo" className="logo" />
      <div className="searchContainer">
        <input
          type="text"
          className="searchInput"
          placeholder="검색"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <button className="searchButton" onClick={handleSearchClick}>
          검색
        </button>
      </div>
      <div className="greetingText">Autumn님을 위한 취향저격 영화를 찾아봤어요.</div>
      <div className="tabButtons">
        <button
          className={activeTab === 'recommendations' ? 'tabButton active' : 'tabButton'}
          onClick={() => setActiveTab('recommendations')}
        >
          내 취향 영화 추천받기
        </button>
        <button
          className={activeTab === 'platforms' ? 'tabButton active' : 'tabButton'}
          onClick={() => setActiveTab('platforms')}
        >
          내 맞춤 플랫폼 찾아보기
        </button>
      </div>
      <div className="tabContent">
        {activeTab === 'recommendations' && topMovie && (
          <MvBanner title={topMovie.title} poster={topMovie.poster_path} />
        )}
        {activeTab === 'platforms' && (
          <div>
            {/* 플랫폼 관련 컨텐츠 */}
          </div>
        )}
      </div>
      <div className="randomMoviesContainer">
        <Swiper
          spaceBetween={10}
          slidesPerView={4}
          navigation
          pagination={{ clickable: true }}
        >
          {randomMovies.map((movie, index) => (
            <SwiperSlide key={index}>
              <MvBanner title={movie.title} poster={movie.poster_path} />
            </SwiperSlide>
          ))}
        </Swiper>
      </div>
    </div>
  );
}

export default RecomPage;
