import React, { useState } from 'react';
import MvBanner from './MvBanner'; // MvBanner 컴포넌트를 임포트합니다.
import { mockMovies } from './Mockmv'; // 목업 데이터를 임포트합니다.
import netflixLogo from '../netflix.png'; // 넷플릭스 로고 이미지
import disneyPlusLogo from '../disneyplus.png'; // 디즈니 플러스 로고 이미지
import watchaLogo from '../watcha.png'; // 왓챠 로고 이미지
import wavveLogo from '../wavve.png'; // 웨이브 로고 이미지

const MovieSearchPage = () => {
  const [selectedGenre, setSelectedGenre] = useState('All');
  const [selectedPlatform, setSelectedPlatform] = useState('All');

  // 장르 버튼 데이터
  const genres = [
    'All', 'Action', 'Adventure', 'Animation', 'Comedy', 
    'Crime', 'Documentary', 'Drama', 'Family', 'Fantasy', 
    'History', 'Horror', 'Music', 'Mystery', 'Romance', 
    'Sci-Fi', 'TV Movie', 'Thriller', 'War', 'Western'
  ];

  // 플랫폼 버튼 데이터 및 로고
  const platforms = [
    { name: 'All', logo: null },
    { name: 'Netflix', logo: netflixLogo },
    { name: 'Disney Plus', logo: disneyPlusLogo },
    { name: 'Watcha', logo: watchaLogo },
    { name: 'Wavve', logo: wavveLogo }
  ];

  // 장르 선택 핸들러
  const handleGenreSelect = (genre) => {
    setSelectedGenre(genre);
  };

  // 플랫폼 선택 핸들러
  const handlePlatformSelect = (platform) => {
    setSelectedPlatform(platform);
  };

  // 필터링된 영화 목록 계산
  const filteredMovies = mockMovies.filter(movie => {
    return (selectedGenre === 'All' || movie.genre.includes(selectedGenre)) &&
           (selectedPlatform === 'All' || movie.flatrate.includes(selectedPlatform));
  });

  return (
    <div>
      <div style={{ display: 'grid', gridTemplateColumns: 'repeat(4, 1fr)', gap: '10px', marginBottom: '20px' }}>
        {genres.map(genre => (
          <button key={genre} onClick={() => handleGenreSelect(genre)} style={{ padding: '10px', cursor: 'pointer', backgroundColor: selectedGenre === genre ? 'yellow' : 'lightgray' }}>
            {genre}
          </button>
        ))}
      </div>
      <div style={{ display: 'grid', gridTemplateColumns: 'repeat(4, 1fr)', gap: '10px', marginBottom: '20px' }}>
        {platforms.map(({ name, logo }) => (
          <button key={name} onClick={() => handlePlatformSelect(name)} style={{ padding: '10px', display: 'flex', alignItems: 'center', backgroundColor: selectedPlatform === name ? 'yellow' : 'lightgray' }}>
            {logo && <img src={logo} alt={name} style={{ marginRight: '10px', width: '20px' }} />}
            {name}
          </button>
        ))}
      </div>
      <div>
        {filteredMovies.map(movie => (
          <MvBanner key={movie.movie_id} title={movie.title} poster={movie.poster_path} flatrate={movie.flatrate} />
        ))}
      </div>
    </div>
  );
};

export default MovieSearchPage;
