import React, { useState } from 'react';  // useState를 import
import { Link } from 'react-router-dom';
import MvBanner from './MvBanner';
import '../css/MvchoPage.css';
import logoImage from '../logo.png';
import { mockMovies } from './Mockmv';

function MvchoPage() {
  const genres = ['장르 전체', '스릴러', 'SF', '미스터리', '애니메이션', '로맨스', '판타지', '다큐멘터리', '코미디', '공포'];
  const [selectedGenre, setSelectedGenre] = useState('장르 전체'); // 선택된 장르 상태 추가

  // 선택된 장르에 따라 필터링된 영화 데이터를 생성
  const filteredMovies = selectedGenre === '장르 전체' ? mockMovies : mockMovies.filter(movie =>
    movie.genre.split(', ').includes(selectedGenre)
  );

  // 필터링된 데이터를 사용하여 배너 생성
  const banners = filteredMovies.map((movie, index) => (
    <MvBanner
      key={index}
      title={movie.title}
      poster={movie.poster_path}
      flatrate={movie.flatrate}
      rating={Math.round(movie.vote_average / 2)}
    />
  ));

  return (
    <div className="MvchoPage">
      <img src={logoImage} alt="Logo" className="logo"/>
      <div className="mainText">재미있게 봤거나 눈길이 가는 영화들을 평가해주세요.</div>
      <div className="subText">찜한 영화들을 바탕으로 MOVIELY가 취향저격 영화들을 추천해 드려요.</div>
      <div className="stickyTop">
        <Link to="/recommendations">
          <button className="recommendButton">영화 추천 받기 &gt;</button>
        </Link>
        <div className="genreButtons">
          {genres.map((genre) => (
            <button key={genre} className="genreButton" onClick={() => setSelectedGenre(genre)}>{genre}</button>
          ))}
        </div>
      </div>
      <div className="bannerGrid">
        {banners.length > 0 ? banners : <div className="noMovies">선택하신 장르의 영화가 없습니다.</div>}
      </div>
    </div>
  );
}

export default MvchoPage;
