import React, { useEffect, useCallback } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { GoogleLogin } from 'react-google-login';
import '../css/LogSignPage.css';
import logoImage from '../logo.png';
import { useAuth } from '../context/AuthContext';

const CLIENT_ID = '13094654813-nqglekemv1iff66rsq4luqftbfgv51hi.apps.googleusercontent.com'; // 실제 Google 클라이언트 ID로 대체

function LogSignPage() {
  const navigate = useNavigate();
  const { login } = useAuth();

  const handleGoogleSuccess = useCallback(async (response) => {
    const { code } = response;

    try {
      const res = await fetch(`https://moviely.duckdns.org/api/login?code=${code}`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
      });
      const data = await res.json();

      if (data.token) {
        login(data.token, data.user); // 로그인 시 사용자 정보 저장
        if (data.isNewUser) {
          navigate('/add');
        } else {
          navigate('/movie-select');
        }
      } else {
        alert('로그인에 실패했습니다.');
      }
    } catch (error) {
      console.error('Backend redirection failed:', error);
      alert('백엔드 처리에 실패했습니다.');
    }
  }, [login, navigate]);

  const handleGoogleFailure = useCallback((error) => {
    console.error('Google Login Failed:', error);
    alert('Google 로그인에 실패했습니다.');
  }, []);

  useEffect(() => {
    const urlParams = new URLSearchParams(window.location.search);
    const code = urlParams.get('code');
    const error = urlParams.get('error');

    if (code) {
      handleGoogleSuccess({ code });
    } else if (error) {
      alert('Google 로그인에 실패했습니다.');
    }
  }, [handleGoogleSuccess]);

  return (
    <div className="logSignPage">
      <Link to="/" className="logSign-logo">
        <img src={logoImage} alt="Logo" />
      </Link>
      <div className="logSignBox">
        <h2>로그인 및 회원가입</h2>
        <p>소셜 로그인 및 회원가입으로<br />MOVIELY의 모든 서비스를 이용하실 수 있습니다.</p>
        <GoogleLogin
          clientId={CLIENT_ID}
          buttonText="Google로 시작하기"
          onSuccess={handleGoogleSuccess}
          onFailure={handleGoogleFailure}
          cookiePolicy={'single_host_origin'}
          responseType="code"
          redirectUri="https://moviely.duckdns.org/login/oauth2/code/google"
        />
      </div>
    </div>
  );
}

export default LogSignPage;
