import React, { useState, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import '../css/MvsrchPage.css';
import logoImage from '../logo.png';

function MvsrchPage() {
  const navigate = useNavigate();
  const [searchTerm, setSearchTerm] = useState('');
  const [showGenres, setShowGenres] = useState(false);
  const [showPlatforms, setShowPlatforms] = useState(false);
  const genreButtonRef = useRef(null);
  const platformButtonRef = useRef(null);

  const genres = ['전체', '액션', '모험', '애니메이션', '코미디', '범죄', '다큐멘터리', '드라마', '가족', '판타지', '역사', '공포', '음악', '미스터리', '로맨스', 'SF', 'TV 영화', '스릴러', '전쟁', '서부'];
  const platforms = ['전체', 'Netflix', 'Disney Plus', 'Watcha', 'Wavve'];

  const handleSearchClick = () => {
    navigate('/movie-search', { state: { searchTerm } });
  };

  return (
    <div className="MvsrchPage">
      <img src={logoImage} alt="Logo" className="logo"/>
      <div className="searchContainer">
        {/* 플랫폼 버튼을 장르 버튼 앞으로 옮겼습니다. */}
        <button ref={platformButtonRef} onClick={() => setShowPlatforms(!showPlatforms)} className="platformButton">플랫폼</button>
        {showPlatforms && (
          <div className="platformDropdown" style={{ top: platformButtonRef.current.offsetHeight + 5, left: platformButtonRef.current.offsetLeft }}>
            {platforms.map((platform) => (
              <button key={platform} onClick={() => setShowPlatforms(false)} className="filter">
                {platform}
              </button>
            ))}
          </div>
        )}
        <button ref={genreButtonRef} onClick={() => setShowGenres(!showGenres)} className="genreButton">장르</button>
        {showGenres && (
          <div className="genreDropdown" style={{ top: genreButtonRef.current.offsetHeight + 5, left: genreButtonRef.current.offsetLeft }}>
            {genres.map((genre) => (
              <button key={genre} onClick={() => setShowGenres(false)} className="filter">
                {genre}
              </button>
            ))}
          </div>
        )}
        <input
          type="text"
          className="searchInput"
          placeholder="검색"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <button className="searchButton" onClick={handleSearchClick}>검색</button>
      </div>
    </div>
  );
}

export default MvsrchPage;
