import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Papa from 'papaparse';
import { Swiper, SwiperSlide } from 'swiper/react';
import 'swiper/css';
import 'swiper/css/navigation';
import 'swiper/css/pagination';
import { Navigation, Pagination } from 'swiper/modules';
import MvBanner from './MvBanner';
import '../css/RecomPage.css';
import logoImage from '../logo.png';
import moviesCSV from '../movie.csv';

function RecomPage() {
  const navigate = useNavigate();
  const [searchTerm, setSearchTerm] = useState('');
  const [activeTab, setActiveTab] = useState('recommendations');
  const [randomMovies, setRandomMovies] = useState([]);
  const [topMovie, setTopMovie] = useState(null);
  const [movieItems, setMovieItems] = useState([]);

  useEffect(() => {
    const fetchRecommendations = async () => {
      try {
        const response = await fetch('http://43.203.39.119:8080/api/recommend', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({ user_id: 1 }), // 유저 아이디를 요청에 포함
        });

        if (response.ok) {
          const data = await response.json();
          if (data.length > 0) {
            setTopMovie(data[0]);
            setMovieItems(data.slice(1, 6)); // Adjust this as needed
          }
        } else {
          console.error('Failed to fetch recommendations:', response.statusText);
        }
      } catch (error) {
        console.error('Error fetching recommendations:', error);
      }
    };

    fetchRecommendations();

    Papa.parse(moviesCSV, {
      download: true,
      header: true,
      complete: (result) => {
        const processedData = result.data.map(movie => ({
          ...movie,
          flatrate: movie.flatrate ? movie.flatrate.split(', ') : [],
          genre: movie.genre ? movie.genre.split(', ') : []
        }));
        setRandomMovies(shuffleArray(processedData).slice(0, 10));
      },
    });
  }, []);

  const shuffleArray = (array) => {
    let shuffled = array.slice();
    for (let i = shuffled.length - 1; i > 0; i--) {
      const j = Math.floor(Math.random() * (i + 1));
      [shuffled[i], shuffled[j]] = [shuffled[j], shuffled[i]];
    }
    return shuffled;
  };

  const handleSearchClick = () => {
    navigate('/movie-search', { state: { searchTerm } });
  };

  return (
    <div className="RecomPage">
      <img src={logoImage} alt="Logo" className="logo" />
      <div className="searchContainer">
        <input
          type="text"
          className="searchInput"
          placeholder="검색"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <button className="searchButton" onClick={handleSearchClick}>
          검색
        </button>
      </div>
      <div className="greetingText">Autumn님을 위한 취향저격 영화를 찾아봤어요.</div>
      <div className="tabButtons">
        <button
          className={activeTab === 'recommendations' ? 'tabButton active' : 'tabButton'}
          onClick={() => setActiveTab('recommendations')}
        >
          내 취향 영화 추천받기
        </button>
        <button
          className={activeTab === 'platforms' ? 'tabButton active' : 'tabButton'}
          onClick={() => setActiveTab('platforms')}
        >
          내 맞춤 플랫폼 찾아보기
        </button>
      </div>
      <div className="tabContent">
        {activeTab === 'recommendations' && (
          <>
            <div className="topMovieContainer">
              {topMovie && (
                <div className="topMovie">
                  <MvBanner
                    title={topMovie.title}
                    poster={topMovie.poster_path}
                    flatrate={topMovie.flatrate.join(', ')}
                    className="topMovieBanner"
                  />
                </div>
              )}
              <div className="movieListContainer">
                {movieItems.map((movie, index) => (
                  <div key={index} className="movieItem">
                    <img
                      src={`http://image.tmdb.org/t/p/w500${movie.poster_path}`}
                      alt={movie.title}
                      className="moviePoster"
                    />
                    <div className="movieDetails">
                      <div className="movieTitle">{movie.title}</div>
                      <div className="movieGenre">{movie.genre.join(', ')}</div>
                      <div className="movieOverview">{movie.overview}</div>
                    </div>
                  </div>
                ))}
              </div>
            </div>
            <div className="randomMoviesContainer">
              <Swiper
                spaceBetween={0}
                slidesPerView={4}
                navigation
                pagination={{ clickable: true }}
                modules={[Navigation, Pagination]}
              >
                {randomMovies.map((movie, index) => (
                  <SwiperSlide key={index}>
                    <div style={{ transform: 'scale(0.8)' }}>
                      <MvBanner title={movie.title} poster={movie.poster_path} flatrate={movie.flatrate.join(', ')} />
                    </div>
                  </SwiperSlide>
                ))}
              </Swiper>
            </div>
          </>
        )}
        {activeTab === 'platforms' && (
          <div>
            {/* 플랫폼 관련 컨텐츠 */}
          </div>
        )}
      </div>
    </div>
  );
}

export default RecomPage;
