import React, { useState, useEffect, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import { AuthContext } from './AuthContext';
import MvBanner from './MvBanner';
import '../css/MvsrchPage.css';
import logoImage from '../logo.png';
import watchaLogo from '../watcha.png';
import netflixLogo from '../netflix.png';
import disneyPlusLogo from '../disneyplus.png';
import wavveLogo from '../wavve.png'; 

const genreMapping = {
  '28': '액션',
  '12': '모험',
  '16': '애니메이션',
  '35': '코미디',
  '80': '범죄',
  '99': '다큐멘터리',
  '18': '드라마',
  '10751': '가족',
  '14': '판타지',
  '36': '역사',
  '27': '공포',
  '10402': '음악',
  '9648': '미스터리',
  '10749': '로맨스',
  '878': 'SF',
  '10770': 'TV 영화',
  '53': '스릴러',
  '10752': '전쟁',
  '37': '서부'
};

function MvsrchPage() {
  const navigate = useNavigate();
  const [searchTerm, setSearchTerm] = useState('');
  const [showPlatforms, setShowPlatforms] = useState(false); 
  const [showGenres, setShowGenres] = useState(false); 
  const [selectedPlatform, setSelectedPlatform] = useState('전체');
  const [selectedGenre, setSelectedGenre] = useState('전체');
  const [movies, setMovies] = useState([]);
  const [loading, setLoading] = useState(false);
  const { user } = useContext(AuthContext);

  const genres = ['전체', '액션', '모험', '애니메이션', '코미디', '범죄', '다큐멘터리', '드라마', '가족', '판타지', '역사', '공포', '음악', '미스터리', '로맨스', 'SF', 'TV 영화', '스릴러', '전쟁', '서부'];
  const platforms = [
    { name: '전체', logo: null },
    { name: '넷플릭스', logo: netflixLogo },
    { name: '디즈니플러스', logo: disneyPlusLogo },
    { name: '왓챠', logo: watchaLogo },
    { name: '웨이브', logo: wavveLogo }
  ];

  useEffect(() => {
    const fetchMovies = async () => {
      setLoading(true);
      try {
        const response = await fetch('http://43.203.39.119:8080/api/movies', {
          method: 'GET',
          headers: {
            'Content-Type': 'application/json'
          }
        });
        if (response.ok) {
          const data = await response.json();
          if (data && Array.isArray(data.content)) {
            const processedData = data.content.map(movie => ({
              ...movie,
              flatrate: movie.flatrate ? movie.flatrate.split(', ') : [],
              genre: movie.genre ? movie.genre.split(', ').map(g => genreMapping[g.trim()]).filter(Boolean) : [] // 장르 매핑 추가
            }));
            setMovies(processedData);
          } else {
            console.error('Data is not an array:', data);
          }
        } else {
          console.error('Failed to fetch movies:', response.statusText);
        }
      } catch (error) {
        console.error('Error fetching movies:', error);
      }
      setLoading(false);
    };

    fetchMovies();
  }, []);

  const handleSearchClick = async () => {
    setLoading(true);
    try {
      const queryParams = new URLSearchParams({
        searchTerm, 
        platform: selectedPlatform === '전체' ? '' : selectedPlatform, 
        genre: selectedGenre === '전체' ? '' : selectedGenre 
      }).toString();

      const response = await fetch(`http://43.203.39.119:8080/api/movies?${queryParams}`, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json'
        }
      });
      if (response.ok) {
        const data = await response.json();
        if (data && Array.isArray(data.content)) {
          const processedData = data.content.map(movie => ({
            ...movie,
            flatrate: movie.flatrate ? movie.flatrate.split(', ') : [],
            genre: movie.genre ? movie.genre.split(', ').map(g => genreMapping[g.trim()]).filter(Boolean) : [] // 장르 매핑 추가
          }));
          setMovies(processedData);
        } else {
          console.error('Data is not an array:', data);
        }
      } else {
        console.error('Failed to fetch movies:', response.statusText);
      }
    } catch (error) {
      console.error('Error fetching movies:', error);
    }
    setLoading(false);
  };

  const handleGenreClick = (genre) => {
    setSelectedGenre(genre);
    setLoading(true);
    fetchMovies();
  };

  const handlePlatformClick = (platform) => {
    setSelectedPlatform(platform);
    setLoading(true);
    fetchMovies();
  };

  // Construct the result text
  let resultText = '';
  if (selectedPlatform !== '전체') {
    resultText += `플랫폼 : <span style="color: yellow;">${selectedPlatform}</span>`;
  }
  if (selectedGenre !== '전체') {
    if (resultText) {
      resultText += ', ';
    }
    resultText += `장르 : <span style="color: yellow;">${selectedGenre}</span>`;
  }
  if (resultText) {
    resultText += ' 검색 결과입니다.';
  }

  return (
    <div className="MvsrchPage">
      <img src={logoImage} alt="Logo" className="logo"/>
      <div className="searchContainer">
        <div className="dropdownContainer">
          <button onClick={() => setShowPlatforms(!showPlatforms)} className="platformButton">플랫폼ㅤㅤㅤ▼</button>
          {showPlatforms && (
            <div className="platformDropdown">
              {platforms.map((platform) => (
                <button 
                  key={platform.name} 
                  onClick={() => { handlePlatformClick(platform.name); setShowPlatforms(false); }} 
                  className="filter"
                >
                  {platform.logo && <img src={platform.logo} alt={platform.name} className="logoIcon"/>}
                  {platform.name}
                </button>
              ))}
            </div>
          )}
        </div>
        <div className="dropdownContainer">
          <button onClick={() => setShowGenres(!showGenres)} className="genreButton">장르ㅤㅤㅤㅤ▼</button>
          {showGenres && (
            <div className="genreDropdown">
              {genres.map((genre) => (
                <button 
                  key={genre} 
                  onClick={() => { handleGenreClick(genre); setShowGenres(false); }} 
                  className="filter"
                >
                  {genre}
                </button>
              ))}
            </div>
          )}
        </div>
        <input
          type="text"
          className="searchInput"
          placeholder="검색"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <button className="searchButton" onClick={handleSearchClick}>검색</button>
      </div>
      <div className="resultText" style={{ marginTop: '200px' }}>
        <span dangerouslySetInnerHTML={{ __html: resultText }} />
      </div>
      <div className="movieResults">
        {loading ? (
          <div>Loading...</div>
        ) : (
          <div className="bannerGrid">
            {movies.length > 0 ? (
              movies.map((movie, index) => (
                <MvBanner
                  key={index}
                  title={movie.title}
                  poster={movie.poster_path}
                  flatrate={movie.flatrate.join(', ')} 
                  rating={Math.round(movie.vote_average / 2)}
                />
              ))
            ) : (
              <div className="noMovies">검색 결과가 없습니다.</div>
            )}
          </div>
        )}
      </div>
    </div>
  );
}

export default MvsrchPage;
