import React, { useState, useContext } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { AuthContext } from './AuthContext';
import '../css/LoginPage.css';
import logoImage from '../logo.png';

function LoginPage() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();
  const { login } = useContext(AuthContext);

  const handleLogin = async (e) => {
    e.preventDefault();
    const csrfToken = document.querySelector('input[name="_csrf"]').value; 

    try {
      const response = await fetch('http://43.203.39.119:8080/api/login', { 
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'X-CSRF-TOKEN': csrfToken 
        },
        body: JSON.stringify({ user_Email: email, user_Pwd: password })
      });

      const text = await response.text();
      console.log('Login response text:', text); 

      try {
        const data = JSON.parse(text);
        if (response.ok) {
          console.log('Login response data:', data); 
          login(data); 
          navigate('/movie-select');
        } else {
          console.error('Login failed:', data);
          alert('로그인에 실패했습니다.');
        }
      } catch (jsonError) {
        console.error('Failed to parse JSON:', text); 
        alert('로그인 중 오류가 발생했습니다.');
      }
    } catch (error) {
      console.error('Error:', error);
      alert('로그인 중 오류가 발생했습니다.');
    }
  };

  return (
    <div className="loginPage">
      <img src={logoImage} alt="Logo" className="logo"/>
      <div className="loginBox">
        <h2>로그인</h2>
        <form onSubmit={handleLogin}>
          <input type="hidden" name="_csrf" value="<%= _csrf.token %>" /> {/* CSRF 토큰 필드 */}
          <div className="inputGroup">
            <label htmlFor="email">이메일 입력</label>
            <input 
              type="email" 
              id="email" 
              name="user_email" 
              placeholder="이메일을 입력하세요" 
              value={email} 
              onChange={(e) => setEmail(e.target.value)} 
            />
          </div> 
          <div className="inputGroup">
            <label htmlFor="password">비밀번호 입력</label>
            <input 
              type="password" 
              id="password" 
              name="user_pwd" 
              placeholder="비밀번호를 입력하세요" 
              value={password} 
              onChange={(e) => setPassword(e.target.value)} 
            />
          </div>
          <button type="submit" className="loginButton">로그인하기</button>
        </form>
        <Link to="/signin" className="signupLink">아직 MOVIELY 계정이 없다면? 회원가입하기</Link>
      </div>
    </div>
  );
}

export default LoginPage;
